CREATE DATABASE `onlineshop` CHARACTER SET utf8 COLLATE utf8_general_ci;USE `onlineshop`;DROP TABLE IF EXISTS `cart`;CREATE TABLE `cart` (	`cart_id` INT (11) NOT NULL AUTO_INCREMENT,	`user_id` INT (11) NOT NULL,	`product_id` INT (11) NOT NULL,	`price` DECIMAL (15, 4) NOT NULL DEFAULT 0.0000,	`quantity` INT (11) NOT NULL,	PRIMARY KEY (`cart_id`)) ENGINE = MyISAM DEFAULT CHARSET = utf8 COLLATE = utf8_general_ci;DROP TABLE IF EXISTS `category`;CREATE TABLE `category` (	`category_id` INT (11) NOT NULL AUTO_INCREMENT,	`parent_id` INT (11) NOT NULL DEFAULT 0,	`name` VARCHAR (128) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,	`sort_order` INT (11) NOT NULL DEFAULT 0,	`status` TINYINT (1) NOT NULL DEFAULT 1,	PRIMARY KEY (`category_id`)) ENGINE = MyISAM DEFAULT CHARSET = utf8 COLLATE = utf8_general_ci;INSERT INTO `category` (`category_id`, `parent_id`, `name`, `sort_order`, `status`) VALUES ('3', '0', 'Electronics', '0', '1');INSERT INTO `category` (`category_id`, `parent_id`, `name`, `sort_order`, `status`) VALUES ('1', '0', 'Laptop', '0', '1');INSERT INTO `category` (`category_id`, `parent_id`, `name`, `sort_order`, `status`) VALUES ('2', '0', 'Phone', '0', '1');UPDATE `category` SET `category_id`='0' WHERE (`category_id`='3') LIMIT 1;DROP TABLE IF EXISTS `manufacturer`;CREATE TABLE `manufacturer` (	`manufacturer_id` INT (11) NOT NULL AUTO_INCREMENT,	`name` VARCHAR (128) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,	`sort_order` INT (11) NOT NULL DEFAULT 0,	`status` TINYINT (1) NOT NULL DEFAULT 1,	PRIMARY KEY (`manufacturer_id`)) ENGINE = MyISAM DEFAULT CHARSET = utf8 COLLATE = utf8_general_ci;DROP TABLE IF EXISTS `order`;CREATE TABLE `order` (	`order_id` INT (11) NOT NULL AUTO_INCREMENT,	`user_id` INT (11) NOT NULL,	`amount` DECIMAL (15, 4) NOT NULL,	`payment_id` INT (11) NOT NULL,	`date_added` datetime(3) NOT NULL DEFAULT '1970-01-01 00:00:00.000',	`order_status_id` INT (11) NOT NULL DEFAULT 0,	`date_modified` datetime(3) NOT NULL DEFAULT '1970-01-01 00:00:00.000',	`tracking_number` VARCHAR (128) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '',	PRIMARY KEY (`order_id`)) ENGINE = MyISAM DEFAULT CHARSET = utf8 COLLATE = utf8_general_ci;DROP TABLE IF EXISTS `ordered_product`;CREATE TABLE `ordered_product` (	`ordered_product_id` INT (11) NOT NULL AUTO_INCREMENT,	`order_id` INT (11) NOT NULL,	`product_id` INT (11) NOT NULL,	`price` DECIMAL (15, 4) NOT NULL,	`quantity` INT (11) NOT NULL,	PRIMARY KEY (`ordered_product_id`)) ENGINE = MyISAM DEFAULT CHARSET = utf8 COLLATE = utf8_general_ci;DROP TABLE IF EXISTS `order_status`;CREATE TABLE `order_status` (	`order_status_id` INT (11) NOT NULL AUTO_INCREMENT,	`name` VARCHAR (128) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,	PRIMARY KEY (`order_status_id`)) ENGINE = MyISAM DEFAULT CHARSET = utf8 COLLATE = utf8_general_ci;INSERT INTO `order_status` (`order_status_id`, `name`) VALUES ('9', 'NEW');INSERT INTO `order_status` (`order_status_id`, `name`) VALUES ('1', 'PENDING');INSERT INTO `order_status` (`order_status_id`, `name`) VALUES ('2', 'PAYMENT_FAILED');INSERT INTO `order_status` (`order_status_id`, `name`) VALUES ('3', 'PAYMENT_RECEIVED');INSERT INTO `order_status` (`order_status_id`, `name`) VALUES ('4', 'PROCESSING');INSERT INTO `order_status` (`order_status_id`, `name`) VALUES ('5', 'SHIPPED');INSERT INTO `order_status` (`order_status_id`, `name`) VALUES ('6', 'DELIVERED');INSERT INTO `order_status` (`order_status_id`, `name`) VALUES ('7', 'COMPLETED');INSERT INTO `order_status` (`order_status_id`, `name`) VALUES ('8', 'CANCELED');UPDATE `order_status` SET `order_status_id`='0' WHERE (`order_status_id`='9') LIMIT 1;DROP TABLE IF EXISTS `payment`;CREATE TABLE `payment` (	`payment_id` INT (11) NOT NULL AUTO_INCREMENT,	`user_id` INT (11) NOT NULL,	`firstname` VARCHAR (128) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,	`lastname` VARCHAR (128) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,	`postcode` INT (11) NOT NULL,	`address` VARCHAR (128) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,	`city` VARCHAR (128) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,	`country` VARCHAR (128) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,	`region` VARCHAR (128) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,	`date_added` datetime(3) NOT NULL DEFAULT '1970-01-01 00:00:00.000',	PRIMARY KEY (`payment_id`)) ENGINE = MyISAM DEFAULT CHARSET = utf8 COLLATE = utf8_general_ci;DROP TABLE IF EXISTS `product`;CREATE TABLE `product` (	`product_id` INT (11) NOT NULL AUTO_INCREMENT,	`name` VARCHAR (128) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,	`description` LONGTEXT CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,	`manufacturer` VARCHAR (128) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,	`category_id` INT (11) NOT NULL,	`price` DECIMAL (15, 4) NOT NULL DEFAULT 0.0000,	`quantity` INT (11) NOT NULL DEFAULT 0,	`image` VARCHAR (128) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,	`date_added` datetime(3) NOT NULL DEFAULT '1970-01-01 00:00:00.000',	`sort_order` INT (11) NOT NULL DEFAULT 0,	`status` TINYINT (1) NOT NULL DEFAULT 1,	PRIMARY KEY (`product_id`)) ENGINE = MyISAM DEFAULT CHARSET = utf8 COLLATE = utf8_general_ci;INSERT INTO `product` (`product_id`, `name`, `description`, `manufacturer`, `category_id`, `price`, `quantity`, `image`, `date_added`, `sort_order`, `status`) VALUES ('9', 'Apple Cinema 30', 'The 30-inch Apple Cinema HD Display delivers an amazing 2560 x 1600 pixel resolution. Designed specifically for the creative professional, this display provides more space for easier access to all the tools and palettes needed to edit, format and composite your work. Combine this display with a Mac Pro, MacBook Pro, or PowerMac G5 and there\'s no limit to what you can achieve.', 'Apple', '0', '110.0000', '100', '/image/catalog/apple_cinema_30.jpg', '2019-06-25 22:54:50', '0', '1');INSERT INTO `product` (`product_id`, `name`, `description`, `manufacturer`, `category_id`, `price`, `quantity`, `image`, `date_added`, `sort_order`, `status`) VALUES ('8', 'HP LP3065', 'Stop your co-workers in their tracks with the stunning new 30-inch diagonal HP LP3065 Flat Panel Monitor. This flagship monitor features best-in-class performance and presentation features on a huge wide-aspect screen while letting you work as comfortably as possible - you might even forget you\'re at the office', 'Hewlett Packard', '1', '122.0000', '100', '/image/catalog/hp.jpg', '2019-06-25 22:58:02', '0', '1');INSERT INTO `product` (`product_id`, `name`, `description`, `manufacturer`, `category_id`, `price`, `quantity`, `image`, `date_added`, `sort_order`, `status`) VALUES ('7', 'Canon EOS 5D', 'Canon\'s press material for the EOS 5D states that it \'defines (a) new D-SLR category\', while we\'re not typically too concerned with marketing talk this particular statement is clearly pretty accurate. The EOS 5D is unlike any previous digital SLR in that it combines a full-frame (35 mm sized) high resolution sensor (12.8 megapixels) with a relatively compact body (slightly larger than the EOS 20D', 'Canon', '0', '98.0000', '100', '/image/catalog/canon_eos_5d.jpg', '2019-06-25 22:56:44', '0', '1');DROP TABLE IF EXISTS `user`;CREATE TABLE `user` (  `user_id`       INT(11)    NOT NULL AUTO_INCREMENT,  `user_group_id` INT(11)    NOT NULL,  `username`      VARCHAR(128) CHARACTER SET utf8  COLLATE utf8_general_ci    NOT NULL,  `password`      VARCHAR(128) CHARACTER SET utf8  COLLATE utf8_general_ci    NOT NULL,  `email`         VARCHAR(128) CHARACTER SET utf8  COLLATE utf8_general_ci    NOT NULL,  `locale`         VARCHAR(128) CHARACTER SET utf8  COLLATE utf8_general_ci    NOT NULL,  `status`        TINYINT(1) NOT NULL DEFAULT 1,  `date_added`    datetime(3)   NOT NULL DEFAULT '1970-01-01 00:00:00.000',	PRIMARY KEY (`user_id`)) ENGINE = MyISAM DEFAULT CHARSET = utf8 COLLATE = utf8_general_ci;INSERT INTO `user` (`user_id`, `user_group_id`, `username`, `password`, `email`, `locale`, `status`, `date_added`) VALUES ('2', '2', 'Admin', 'Admin@1#', 'Admin@Admin.Admin', 'en', '1', '2019-06-21 16:41:31');DROP TABLE IF EXISTS `user_group`;CREATE TABLE `user_group` (	`user_group_id` INT (11) NOT NULL AUTO_INCREMENT,	`name` VARCHAR (64) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,	PRIMARY KEY (`user_group_id`)) ENGINE = MyISAM DEFAULT CHARSET = utf8 COLLATE = utf8_general_ci;INSERT INTO `user_group` (`user_group_id`, `name`) VALUES ('3', 'CUSTOMER');INSERT INTO `user_group` (`user_group_id`, `name`) VALUES ('1', 'MODERATOR');INSERT INTO `user_group` (`user_group_id`, `name`) VALUES ('2', 'ADMINISTRATOR');UPDATE `user_group` SET `user_group_id`='0' WHERE (`user_group_id`='3') LIMIT 1;